mapper.select:
SELECT m.menu_no AS menuNo, c.name AS categoryName, m.menu_name AS menuName, m.menu_price AS menuPrice FROM menu m INNER JOIN categories c ON m.categories_no = c.categories_no
m.soft_delete = 'N' AND c.store_no = #{storeNo}
AND m.menu_name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY m.menu_name LIMIT #{size} OFFSET #{offset}

update.foreach:
UPDATE MENU
SET position = #{item.position}
WHERE menu_no = #{item.menuNo}

mapper.update:
UPDATE MENU

    , menu_name = #{dto.menuName}
    , menu_price = #{dto.menuPrice}
    , categories_no = #{dto.categoriesNo}
    , description = #{dto.description}
    , position = #{dto.position}

WHERE menu_no = #{menuNo}
